// index.js
import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import { CONTRACT_ADDRESS, transformCharacterData } from "../../constants";
import myEpicGame from "../../utils/MyEpicGame.json";
import "./Arena.css";
// フロントエンドにNFTキャラクターを表示するため、characterNFTのメタデータを渡します。
const Arena = ({ characterNFT }) => {
    // コントラクトのデータを保有する状態変数を初期化します。
    const [gameContract, setGameContract] = useState(null);
    // ボスのメタデータを保存する状態変数を初期化します。
    const [boss, setBoss] = useState(null);

    // NFTキャラクターがボスを攻撃する際に使用する関数を定義します。
    const runAttackAction = async () => {};

    // ページがロードされると下記が実行されます。
    useEffect(() => {
        // コントラクトからボスのメタデータを取得し、bossを設定する非同期関数 fetchBoss を設定します。
        const fetchBoss = async () => {
            const bossTxn = await gameContract.getBigBoss();
            console.log("Boss:", bossTxn);
            setBoss(transformCharacterData(bossTxn));
        };
        if (gameContract) {
        // コントラクトの準備ができたら、ボスのメタデータを取得します。
            fetchBoss();
        }
    }, [gameContract]);

    // ページがロードされると下記が実行されます。
    useEffect(() => {
        const { ethereum } = window;
        if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const gameContract = new ethers.Contract(
            CONTRACT_ADDRESS,
            myEpicGame.abi,
            signer
        );
        setGameContract(gameContract);
        } else {
        console.log("Ethereum object not found");
        }
    }, []);
    return (
        <div className="arena-container">
            {/* ボスをレンダリングします */}
            {boss && (
            <div className="boss-container">
                <div className={`boss-content`}>
                    <h2>🔥 {boss.name} 🔥</h2>
                    <div className="image-content">
                        <img src={boss.imageURI} alt={`Boss ${boss.name}`} />
                        <div className="health-bar">
                            <progress value={boss.hp} max={boss.maxHp} />
                            <p>{`${boss.hp} / ${boss.maxHp} HP`}</p>
                        </div>
                    </div>
                </div>
                <div className="attack-container">
                    <button className="cta-button" onClick={runAttackAction}>
                        {`💥 Attack ${boss.name}`}
                    </button>
                </div>
            </div>
            )}
            {/* NFT キャラクター をレンダリングします*/}
            {characterNFT && (
                <div className="players-container">
                    <div className="player-container">
                        <h2>Your Character</h2>
                        <div className="player">
                            <div className="image-content">
                                <h2>{characterNFT.name}</h2>
                                <img
                                    src={characterNFT.imageURI}
                                    alt={`Character ${characterNFT.name}`}
                                />
                                <div className="health-bar">
                                    <progress value={characterNFT.hp} max={characterNFT.maxHp} />
                                    <p>{`${characterNFT.hp} / ${characterNFT.maxHp} HP`}</p>
                                </div>
                            </div>
                            <div className="stats">
                                <h4>{`⚔️ Attack Damage: ${characterNFT.attackDamage}`}</h4>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};
export default Arena;
